mapper.select:
/* 멘토 근무경력승인 목록조회 */ SELECT mwhd.mentor_work_career_details_code AS mentorCode, m.member_id AS mentorId, mwhd.mentor_POWK_NM AS mentorPowk, mwhd.mentor_career_apply_manager AS mentorYn, mwhd.FRST_REG_DT AS mentorDate, mwhd.upload_file_work_history_name AS mentorFileNm FROM member m INNER join mentor_work_history_details mwhd on m.member_id=mwhd.member_id
mwhd.mentor_career_apply_manager ='N'
mwhd.mentor_career_apply_manager !='N'
ORDER BY mwhd.FRST_REG_DT desc
LIMIT #{rowPerPage} OFFSET #{offset};
