	honor_mentor_YN='Y'
WHERE member_id = #{memberId};

mapper.select:
/* 명예멘토 리스트 조회 */ SELECT member_id AS memberId, member_email AS memberEmail, member_name AS memberName, member_phone_number AS memberTelNo, member_join_YMD AS memberJoinYMD FROM `member` WHERE member_id IN
#{honorMenotrId}
LIMIT #{rowPerPage} OFFSET #{offset};
