mapper.select:
/* 멘토링 신청 수 조회 */
SELECT
	COUNT(*)
FROM
	mentoring_apply;

mapper.delete:
/* 멘토링공고 질문 삭제 */
DELETE 
FROM 
	mentoring_notice_question 
WHERE 
	mentoring_notice_question_code=#{questionCode};

/* 멘토링공고 답변 삭제 */
DELETE 
FROM 
	mentoring_notice_question_answer 
WHERE mentoring_notice_question_answer_code=#{answerCode};

mapper.select:
/* 공고목록 행의 갯수 */
SELECT
	COUNT(*)
FROM
	mentoring_notice;

/* 공고 질문 수 조회 */
SELECT
	COUNT(*)
FROM
	mentoring_notice_question;

/* 멘토링 공고 답변 수 조회 */
SELECT
	COUNT(*)
FROM
	mentoring_notice_question_answer;
