mapper.select:
/* 멘토링 신청 조회 */ SELECT mentoring_apply_code, mentoring_notice_code, mentoring_notice_detail_code, member_id, amount, amount_status, update_YMD FROM mentoring_apply
member_id LIKE CONCAT('%', #{searchId}, '%')
ORDER BY update_YMD DESC LIMIT #{rowPerPage} OFFSET #{offset};

mapper.delete:
/* 멘토링공고 질문 삭제 */
DELETE 
FROM 
	mentoring_notice_question 
WHERE 
	mentoring_notice_question_code=#{questionCode};

/* 멘토링공고 답변 삭제 */
DELETE 
FROM 
	mentoring_notice_question_answer 
WHERE mentoring_notice_question_answer_code=#{answerCode};

mapper.select:
/* 공고목록조회 */ SELECT member_id AS memberId, mentoring_notice_code AS noticeCode, mentoring_notice_title AS noticeTitle, mentoring_mentor_PSBLTY_start_YMD AS noticeStartYmd, mentoring_mentor_PSBLTY_end_YMD AS noticeEndYmd, mentor_notice_REG_YMD AS noticeRegYmd FROM mentoring_notice
member_id LIKE CONCAT('%', #{searchId}, '%')
ORDER BY mentor_notice_REG_YMD DESC LIMIT #{rowPerPage} OFFSET #{offset};

/* 멘토링공고 질문 조회 */ SELECT mentoring_notice_question_code, mentoring_notice_code, member_id, question_content, question_writing_YMD FROM mentoring_notice_question
member_id LIKE CONCAT('%', #{searchId}, '%')
ORDER BY question_writing_YMD DESC LIMIT #{rowPerPage} OFFSET #{offset};

/* 멘토링공고 답변 조회 */ SELECT mentoring_notice_question_answer_code, mentoring_notice_question_code, mentor_id, answer_content, answer_writing_YMD FROM mentoring_notice_question_answer
mentor_id LIKE CONCAT('%', #{searchId}, '%')
ORDER BY answer_writing_YMD DESC LIMIT #{rowPerPage} OFFSET #{offset};
