mapper.update:
/* 로그인 되어있는 회원의 계정 정보 수정 */ UPDATE member
member_password = #{menteePw},
member_name = #{menteeNm},
member_phone_number = #{menteeTelNo},
member_email = #{menteeEmail},
WHERE member_id = #{menteeId};

/* 로그인 되어있는 회원의 소개글 수정 */ UPDATE mentee_profile
mentee_profile_code=#{profileCode},
mentee_interest_field=#{interestField}, mentee_interest_field_technology=#{fieldTechnology}, mentee_interest_field_experience_level=#{experienceLevel}, mentee_interest_field_experience_explanation=#{experienceExplanation},
mentee_profile_release_YN=#{releaseYN},
WHERE member_id = #{memberId};
