mapper.select:
/* 멘토의 정산 내역 조회 (연, 월 필터) */ SELECT mn.mentoring_notice_title, mnd.day_mentoring_YMD, mnd.day_mentoring_time, msmc.mentor_calculation_amount, mcdc.calculation_demand_date, mcdc.calculation_give_date, mcdc.calculation_status FROM mentor_calculation_demand_completion mcdc INNER JOIN mentee_settlement_mentor_calculation msmc ON mcdc.mentee_settlement_mentor_calculation_code = msmc.mentee_settlement_mentor_calculation_code INNER JOIN mentoring_apply ma ON msmc.mentoring_apply_code = ma.mentoring_apply_code INNER JOIN mentoring_notice mn ON msmc.mentor_notice_code = mn.mentoring_notice_code INNER JOIN mentoring_notice_detail mnd ON ma.mentoring_notice_detail_code = mnd.mentoring_notice_detail_code WHERE mcdc.mentor_id = #{memberId}
AND YEAR(mcdc.calculation_give_date) = #{selectedYear}
AND MONTH(mcdc.calculation_give_date) = #{selectedMonth}

/* 연도와 월로 결제된 내역 검색 */ SELECT msmc.mentee_settlement_mentor_calculation_code, mn.mentoring_notice_code, mn.topic_category_code, ma.mentoring_apply_code, mn.mentoring_notice_title, mnd.day_mentoring_YMD, mnd.day_mentoring_time, msmc.settlement_id, msmc.mentor_calculation_amount, msmc.mentoring_progress_status FROM mentee_settlement_mentor_calculation msmc INNER JOIN mentoring_apply ma ON msmc.mentoring_apply_code = ma.mentoring_apply_code INNER join mentoring_notice mn ON ma.mentoring_notice_code = mn.mentoring_notice_code INNER JOIN mentoring_notice_detail mnd ON ma.mentoring_notice_detail_code = mnd.mentoring_notice_detail_code WHERE mn.member_id = #{memberId}
AND YEAR(mnd.day_mentoring_YMD) = #{selectedYear}
AND MONTH(mnd.day_mentoring_YMD) = #{selectedMonth}
ORDER BY msmc.settlement_day DESC;

/* 연도와 월로 결제한 내역 검색 */ SELECT msmc.mentee_settlement_mentor_calculation_code, ma.mentoring_apply_code, mn.mentoring_notice_code, mn.topic_category_code, mn.mentoring_notice_title, mnd.day_mentoring_YMD, mnd.day_mentoring_time, mn.member_id, msmc.last_amount, msmc.settlement_day, msmc.mentoring_progress_status FROM mentee_settlement_mentor_calculation msmc INNER JOIN mentoring_apply ma ON msmc.mentoring_apply_code = ma.mentoring_apply_code INNER JOIN mentoring_notice mn ON ma.mentoring_notice_code = mn.mentoring_notice_code INNER JOIN mentoring_notice_detail mnd ON ma.mentoring_notice_detail_code = mnd.mentoring_notice_detail_code WHERE msmc.settlement_id = #{memberId}
AND YEAR(msmc.settlement_day) = #{selectedYear}
AND MONTH(msmc.settlement_day) = #{selectedMonth}
ORDER BY msmc.settlement_day DESC;

/* 멘토링 미결제건 개수 조회 */
SELECT 
	COUNT(mentoring_apply_code)
FROM 
	mentoring_apply
WHERE 
	member_id = #{memberId}
	AND 
	amount_status = '결제 전';
